import {
  SET_PROFESSIONS,
  SET_PROFESSION,
  ADD_PROFESSION,
  SET_COURSES,
  // LIKE_SCREAM,
  // UNLIKE_SCREAM,
  LOADING_DATA,
  LIKE_COURSE,
  UNLIKE_COURSE,
  // DELETE_SCREAM,
  // POST_SCREAM,
  // SET_SCREAM,
  // SUBMIT_COMMENT,
} from "../types";

const initialState = {
  professions: [],
  profession: {},
  courses: [],
  loading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case LOADING_DATA:
      return {
        ...state,
        loading: true,
      };
    case SET_PROFESSIONS:
      return {
        ...state,
        professions: action.payload,
        loading: false,
      };
    case SET_PROFESSION:
      return {
        ...state,
        professionName: action.payload,
        loading: false,
      };

    case SET_COURSES:
      return {
        ...state,
        courses: action.payload,
        loading: false,
      };
    case LIKE_COURSE:
    case UNLIKE_COURSE:
      let index = state.courses.findIndex(
        (course) => course.courseId === action.payload.courseId
      );
      state.courses[index] = action.payload;
      // if (state.scream.screamId === action.payload.screamId) {
