import React, { Component } from "react";
import { connect } from "react-redux";
import Course from "../components/Course";

export class courses extends Component {
  state = {
    entry: "",
    adding: false,
  };
  render() {
    const { courses, professionName } = this.props.data;
    let renderedCourse;

    if (courses.length !== 0) {
      renderedCourse = courses.map((course) => (
        <Course key={course.courseId} course={course} />
      ));
    } else {
      renderedCourse = <p>no courses yet</p>;
    }
    return (
      <div>
        <div>
          <h1>{professionName}</h1>
        </div>
        <div>{renderedCourse}</div>
        <Button
          type="button"
          variant="contained"
          color="secondary"
          className={classes.button}
          disabled={this.state.adding}
          onClick={this.handleAdd}
        >
          ADD NEW PROFESSION
        </Button>
        {this.state.adding && (
          <form>
            <TextField
              id="entry"
              name="entry"
              type="text"
              label="Enter a new course..."
              className={classes.textField}
              helperText={this.state.errors}
              error={this.state.errors ? true : false}
              value={this.state.newCourse}
              onChange={this.handleChange}
              fullWidth
            />
            <Button
              type="submit"
              variant="contained"
              color="secondary"
              className={classes.button}
              disabled={!this.state.adding}
              onClick={this.handleNewSubmit}
            >
              ADD
            </Button>
            <Button
              type="button"
              variant="contained"
              color="primary"
              className={classes.button}
              disabled={!this.state.adding}
              onClick={this.handleCancel}
            >
              CANCEL
            </Button>
          </form>
        )}
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  data: state.data,
});
export default connect(mapStateToProps)(courses);
