import {
  SET_PROFESSIONS,
  SET_PROFESSION,
  ADD_PROFESSION,
  SET_COURSES,
  LOADING_DATA,
  LIKE_COURSE,
  UNLIKE_COURSE,
  DELETE_COMMENT,
  SET_ERRORS,
  POST_COMMENT,
  CLEAR_ERRORS,
  LOADING_UI,
  SET_COURSE,
  STOP_LOADING_UI,
  SUBMIT_COMMENT,
} from "../types";
import axios from "axios";

// Get all screams
export const getProfessions = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/professions")
    .then((res) => {
      dispatch({
        type: SET_PROFESSIONS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_PROFESSIONS,
        payload: [],
      });
    });
};

export const searchCourses = (professionName) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/courses", { params: { professionName } })
    .then((res) => {
      console.log(professionName);
      dispatch({
        type: SET_COURSES,
        payload: res.data,
      });
      dispatch({
        type: SET_PROFESSION,
        payload: professionName,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_COURSES,
        payload: [],
      });
    });
};

export const addProfession = (professionName) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .post("/profession", { name: professionName })
    .then((res) => {
      console.log(professionName);
      dispatch(getProfessions());
    })
    .catch((err) => {
      dispatch({
        type: SET_COURSES,
        payload: [],
      });
    });
};

export const likeCourse = (courseId) => (dispatch) => {
  axios
    .get(`/course/${courseId}/like`)
    .then((res) => {
      dispatch({
        type: LIKE_COURSE,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
// Unlike a scream
export const unlikeCourse = (courseId) => (dispatch) => {
  axios
    .get(`/course/${courseId}/unlike`)
    .then((res) => {
      dispatch({
        type: UNLIKE_COURSE,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
export const addCourse = (courseName, professionName) => (dispatch) => {
  axios
    .post(`/profession/course`, { name: courseName, professionName })
    .then((res) => {
      dispatch(searchCourses(professionName));
    })
    .catch((err) => console.log(err));
};

export const searchComments = (courseId) => (dispatch) => {
  axios
    .get(`/courseToProfession/${courseId}`)
    .then((res) => {
      console.log(res.data);
      dispatch({
        type: SET_COURSE,
        payload: res.dat,
      });
      dispatch({
        type: SET_COMMENTS,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
