import {
  SET_PROFESSIONS,
  SET_PROFESSION,
  ADD_PROFESSION,
  SET_COURSES,
  LOADING_DATA,
  LIKE_COURSE,
  UNLIKE_COURSE,
  DELETE_COMMENT,
  SET_ERRORS,
  POST_COMMENT,
  CLEAR_ERRORS,
  LOADING_UI,
  SET_COURSE,
  STOP_LOADING_UI,
  SUBMIT_COMMENT,
} from "../types";
import axios from "axios";

// Get all screams
export const getProfessions = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/professions")
    .then((res) => {
      dispatch({
        type: SET_PROFESSIONS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_PROFESSIONS,
        payload: [],
      });
    });
};

export const searchCourses = (professionName) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/courses", { params: { professionName } })
    .then((res) => {
      console.log(professionName);
      dispatch({
        type: SET_COURSES,
        payload: res.data,
      });
      dispatch({
        type: SET_PROFESSION,
        payload: professionName,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_COURSES,
        payload: [],
      });
    });
};

export const addProfession = (professionName) => (dispath) => {
  dispatch({ type: LOADING_DATA });
  axios
    .post("/profession", { body: { name: professionName } })
    .then((res) => {
      console.log(professionName);
      dispatch({
        type: SET_COURSES,
        payload: res.data,
      });
      dispatch({
        type: SET_PROFESSION,
        payload: professionName,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_COURSES,
        payload: [],
      });
    });
};
