import {
  SET_PROFESSIONS,
  SET_COURSES,
  LOADING_DATA,
  LIKE_COURSE,
  UNLIKE_COURSE,
  DELETE_COMMENT,
  SET_ERRORS,
  POST_COMMENT,
  CLEAR_ERRORS,
  LOADING_UI,
  SET_COURSE,
  STOP_LOADING_UI,
  SUBMIT_COMMENT,
} from "../types";
import axios from "axios";

// Get all screams
export const getProfessions = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/professions")
    .then((res) => {
      dispatch({
        type: SET_PROFESSIONS,
        payload: res.data,
      });
      history.push("/courses");
    })
    .catch((err) => {
      dispatch({
        type: SET_PROFESSIONS,
        payload: [],
      });
    });
};

export const searchCourses = (name, history) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/courses", { params: { professionName: name } })
    .then((res) => {
      dispatch({
        type: SET_COURSES,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_COURSES,
        payload: [],
      });
    });
};
