import React, { Component } from "react";
import { connect } from "react-redux";
import { getProfessions } from "../redux/actions/dataActions";

import Profession from "./Profession";
import {
  Grid,
  Typography,
  TextField,
  Button,
  CircularProgress,
} from "@material-ui/core";
export class Search extends Component {
  componentDidMount() {
    this.props.getProfessions();
  }
  handleForm = (event) => {
    event.preventDefault();
  };
  render() {
    const { professions, loading } = this.props.data;

    let professionsMarkup = !loading ? (
      professions.map((profession) => (
        <Profession key={profession.professionId} profession={profession} />
      ))
    ) : (
      <p>Loading</p>
    );

    return (
      <Grid container spacing={10}>
        <Grid item sm={2} xs={12} />
        <Grid item sm={8} xs={12}>
          <form noValidate onSubmit={this.handleForm}>
            <TextField
              id="search"
              name="search"
              type="text"
              label="Enter a profession..."
              className={classes.textField}
              helperText={errors.search}
              error={errors.search ? true : false}
              value={this.state.search}
              onChange={this.handleChange}
              fullWidth
            />
            {errors.general && (
              <Typography variant="body2" className={classes.customError}>
                {errors.general}
              </Typography>
            )}
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              disabled={loading}
            >
              SEARCH
              {loading && (
                <CircularProgress size={30} className={classes.progress} />
              )}
            </Button>
          </form>
        </Grid>
        <Grid item sm={2} xs={12} />
      </Grid>
    );
    return <div>{professionsMarkup}</div>;
  }
}

// Search.propTypes = {
//   getProfessions: PropTypes.func.isRequired,
//   data: PropTypes.object.isRequired,
// };

const mapStateToProps = (state) => ({
  data: state.data,
});

export default connect(mapStateToProps, { getProfessions })(Search);
