import React, { Component } from "react";
import { connect } from "react-redux";
import { getProfessions } from "../redux/actions/dataActions";
import withStyles from "@material-ui/core/styles/withStyles";
import Profession from "./Profession";
import {
  Grid,
  Typography,
  TextField,
  Button,
  CircularProgress,
} from "@material-ui/core";

const styles = {
  form: {
    textAlign: "center",
  },
  image: {
    margin: "20px auto 20px auto",
  },
  button: {
    marginTop: 20,
    position: "relative",
  },
  pageTitle: {
    margin: "10px auto 10px auto",
  },
  textField: {
    margin: "10px auto 10px auto",
  },
  customError: {
    color: "red",
    fontSize: "0.8rem",
    marginTop: 10,
  },
  progress: {
    position: "absolute",
  },
};
export class Search extends Component {
  componentDidMount() {
    this.props.getProfessions();
  }
  handleSubmit = (event) => {
    event.preventDefault();
  };
  render() {
    const { professions, loading } = this.props.data;
    const { classes } = this.props;
    let professionsMarkup = !loading ? (
      professions.map((profession) => (
        <Profession key={profession.professionId} profession={profession} />
      ))
    ) : (
      <p>Loading</p>
    );

    return (
      <Grid container spacing={10}>
        <Grid item sm={2} xs={12} />
        <Grid item sm={8} xs={12}>
          <form noValidate onSubmit={this.handleSubmit}>
            <TextField
              id="search"
              name="search"
              type="text"
              label="Enter a profession..."
              className={classes.textField}
              // helperText={errors.search}
              // error={errors.search ? true : false}
              // value={this.state.search}
              // onChange={this.handleChange}
              fullWidth
            />
            {/* {errors.general && (
              <Typography variant="body2" className={classes.customError}>
                {errors.general}
              </Typography>
            )} */}
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              disabled={loading}
            >
              SEARCH
              {loading && (
                <CircularProgress size={30} className={classes.progress} />
              )}
            </Button>
          </form>
          <div>{professionsMarkup}</div>
        </Grid>
        <Grid item sm={2} xs={12} />
      </Grid>
    );
    // return ;
  }
}

// Search.propTypes = {
//   getProfessions: PropTypes.func.isRequired,
//   data: PropTypes.object.isRequired,
// };

const mapStateToProps = (state) => ({
  data: state.data,
});

export default connect(mapStateToProps, { getProfessions })(
  withStyles(styles)(Search)
);
