import React, { Component } from "react";
import MyButton from "../util/MyButton";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
// Icons
import FavoriteIcon from "@material-ui/icons/Favorite";
import FavoriteBorder from "@material-ui/icons/FavoriteBorder";
// REdux
import { connect } from "react-redux";
import { likeCourse, unlikeCourse } from "../redux/actions/dataActions";

export class LikeButton extends Component {
  likedCourse = () => {
    if (
      this.props.user.likes &&
      this.props.user.likes.find(
        (like) => like.courseId === this.props.courseId
      )
    )
      return true;
    else return false;
  };
  likeCourse = () => {
    this.props.likeCourse(this.props.courseId);
  };
  unlikeCourse = () => {
    this.props.unlikeCourse(this.props.courseId);
  };
  render() {
    const { authenticated } = this.props.user;
    const likeButton = !authenticated ? (
      <Link to="/login">
        <MyButton tip="Like">
          <FavoriteBorder color="primary" />
        </MyButton>
      </Link>
    ) : this.likedScream() ? (
      <MyButton tip="Undo like" onClick={this.unlikeCourse}>
        <FavoriteIcon color="primary" />
      </MyButton>
    ) : (
      <MyButton onClick={this.likeCourse} tip="Like">
        <FavoriteBorder color="primary" />
      </MyButton>
    );
    return likeButton;
  }
}

LikeButton.propTypes = {
  user: PropTypes.object.isRequired,
  courseId: PropTypes.string.isRequired,
  likeCourse: PropTypes.func.isRequired,
  unlikeCourse: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  user: state.user,
});

const mapActionsToProps = {
  likeCourse,
  unlikeCourse,
};

export default connect(mapStateToProps, mapActionsToProps)(LikeButton);
